];

interface QuestionnairePageProps {
  onComplete: (analysisResult: any) => void;
}

export function QuestionnairePage({ onComplete }: QuestionnairePageProps) {
  const { session, createSession, updateSession } = useSession();
  const {
    currentStep,
    formData,
    updateFormData,
    nextStep,
    prevStep,
    submitQuestionnaire,
    loadFormDataFromSession,
    isSubmitting,
    analysisResult,
    error,
  } = useQuestionnaire();

  const currentQuestion = questions[currentStep];
  const progress = ((currentStep + 1) / questions.length) * 100;

  // Simple form change handler without complex session logic
  const handleFormChange = (newData: Partial<FinancialData>) => {
    updateFormData(newData);

    // Create/update session only when user enters a real name (length > 1)
    if (newData.name && newData.name.length > 1) {
      if (!session) {
        createSession(newData.name);
      } else if (session.userName !== newData.name) {
        createSession(newData.name);
      }
    }
  };

  const handleNext = () => {
    if (currentStep === questions.length - 1) {
      // Submit the questionnaire
      submitQuestionnaire.mutate(formData as any);
    } else {
      nextStep();
    }
  };

  useEffect(() => {
    if (analysisResult) {
      onComplete(analysisResult);
    }
  }, [analysisResult, onComplete]);

  if (isSubmitting) {
    return (
      <div className="min-h-screen relative flex items-center justify-center">
        <CosmicBackground />
        <div className="text-center space-y-6 relative z-10">
          <div className="animate-spin text-6xl">🛰️</div>
          <h2 className="font-orbitron text-2xl font-bold text-neon-cyan">
            Analyzing Your Financial Universe...
          </h2>
          <p className="text-gray-300">Our AI is processing your data to generate personalized insights</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen relative flex items-center justify-center">
        <CosmicBackground />
        <div className="text-center space-y-6 relative z-10">
          <div className="text-6xl">❌</div>
          <h2 className="font-orbitron text-2xl font-bold text-red-400">
            Mission Failed
          </h2>
          <p className="text-gray-300">There was an error processing your data. Please try again.</p>
          <p className="text-red-400 text-sm">{error.message}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen relative py-20">
      <CosmicBackground />

      <div className="relative z-10">
        <QuestionForm
          question={currentQuestion}
          data={formData}
          onDataChange={handleFormChange}
          onNext={handleNext}
          onPrev={prevStep}
          isFirst={currentStep === 0}
          isLast={currentStep === questions.length - 1}
          progress={progress}
        />
      </div>
    </div>
  );
}

