        >
          <CartesianGrid 
            strokeDasharray="3 3" 
            stroke="#374151" 
            strokeOpacity={0.3}
          />
          <XAxis 
            dataKey="shortName" 
            stroke="#E5E7EB"
            fontSize={12}
            fontWeight="bold"
            tick={{ fill: '#E5E7EB' }}
            tickFormatter={(value, index) => {
              const item = data[index];
              return `${item?.icon || ''} ${value}`;
            }}
          />
          <YAxis 
            stroke="#E5E7EB"
            fontSize={11}
            fontWeight="bold"
            tick={{ fill: '#E5E7EB' }}
            tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
          />
          <Tooltip
            contentStyle={{
