          <Bar 
            dataKey="amount" 
            radius={[8, 8, 0, 0]}
            stroke="#1A1A3A"
            strokeWidth={2}
          >
            {data.map((entry, index) => (
              <Cell 
                key={`cell-${index}`} 
                fill={entry.color}
