import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Line, ComposedChart, Area, AreaChart } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { TrendingUp, Calendar, Target, Zap } from 'lucide-react';

interface SpendingData {
  month: string;
  essential: number;
  lifestyle: number;
  savings: number;
  investments: number;
  total: number;
  budget: number;
}

interface EnhancedSpendingChartProps {
  data: SpendingData[];
  title?: string;
  className?: string;
}

const EnhancedSpendingChart: React.FC<EnhancedSpendingChartProps> = ({ 
  data, 
  title = "Monthly Spending Analysis",
  className = "" 
}) => {
  const [viewMode, setViewMode] = useState<'bar' | 'area' | 'composed'>('composed');
  const [selectedMetric, setSelectedMetric] = useState<string>('total');
  const [isAnimated, setIsAnimated] = useState(false);

  useEffect(() => {
    setIsAnimated(true);
  }, []);

  const categories = [
    { key: 'essential', name: 'Essentials', color: '#00F5FF' },
    { key: 'lifestyle', name: 'Lifestyle', color: '#8B5CF6' },
    { key: 'savings', name: 'Savings', color: '#00FF7F' },
    { key: 'investments', name: 'Investments', color: '#FFD700' },
  ];

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="cosmic-card p-4 border border-neon-cyan/30 backdrop-blur-md">
          <p className="font-orbitron text-neon-cyan font-semibold mb-2">{label}</p>
          {payload.map((entry: any, index: number) => (
            <div key={index} className="flex items-center justify-between mb-1">
              <span className="text-gray-300 text-sm">{entry.name}:</span>
              <span className="font-mono text-neon-cyan ml-2">
                ₹{entry.value.toLocaleString()}
              </span>
            </div>
          ))}
          <div className="border-t border-gray-600 mt-2 pt-2">
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Budget:</span>
              <span className="font-mono text-stellar-gold">
                ₹{payload[0]?.payload?.budget?.toLocaleString()}
              </span>
            </div>
          </div>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    const commonProps = {
      data,
      margin: { top: 20, right: 30, left: 20, bottom: 5 }
    };

    switch (viewMode) {
      case 'bar':
        return (
          <BarChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />
            {categories.map((category) => (
              <Bar
                key={category.key}
                dataKey={category.key}
                fill={category.color}
                radius={[2, 2, 0, 0]}
                fillOpacity={0.8}
              />
            ))}
          </BarChart>
        );

      case 'area':
        return (
          <AreaChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />
            {categories.map((category, index) => (
              <Area
                key={category.key}
                type="monotone"
                dataKey={category.key}
                stackId="1"
                stroke={category.color}
                fill={category.color}
                fillOpacity={0.6}
              />
            ))}
          </AreaChart>
        );

      case 'composed':
      default:
        return (
          <ComposedChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />
            {categories.map((category) => (
              <Bar
                key={category.key}
                dataKey={category.key}
                fill={category.color}
                radius={[2, 2, 0, 0]}
                fillOpacity={0.7}
              />
            ))}
            <Line 
              type="monotone" 
              dataKey="budget" 
              stroke="#FFD700" 
              strokeWidth={3}
              strokeDasharray="5 5"
              dot={{ fill: '#FFD700', strokeWidth: 2, r: 4 }}
            />
          </ComposedChart>
        );
    }
  };

  const totalSpent = data.reduce((sum, month) => sum + month.total, 0);
  const avgMonthlySpending = totalSpent / data.length;
  const totalBudget = data.reduce((sum, month) => sum + month.budget, 0);
  const budgetUtilization = (totalSpent / totalBudget) * 100;

  return (
    <Card className={`cosmic-card transform-3d transition-all duration-500 hover:scale-105 ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="font-orbitron text-xl text-cosmic-gradient">
            {title}
          </CardTitle>
          <div className="flex gap-2">
            {[
              { mode: 'composed' as const, icon: TrendingUp, label: 'Trend' },
              { mode: 'bar' as const, icon: Calendar, label: 'Bars' },
              { mode: 'area' as const, icon: Target, label: 'Area' },
            ].map(({ mode, icon: Icon, label }) => (
              <Button
                key={mode}
                size="sm"
                variant={viewMode === mode ? "default" : "outline"}
                onClick={() => setViewMode(mode)}
                className={`cosmic-button px-3 py-1 ${viewMode === mode ? 'bg-neon-cyan text-slate-900' : 'border-neon-cyan text-neon-cyan'}`}
              >
                <Icon className="w-4 h-4" />
              </Button>
            ))}
          </div>
        </div>

        {/* Summary Stats */}
        <div className="grid grid-cols-3 gap-4 mt-4">
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Total Spent</div>
            <div className="font-mono text-lg text-neon-cyan">₹{totalSpent.toLocaleString()}</div>
          </div>
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Avg Monthly</div>
            <div className="font-mono text-lg text-cosmic-purple">₹{avgMonthlySpending.toLocaleString()}</div>
          </div>
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Budget Usage</div>
            <div className={`font-mono text-lg ${budgetUtilization > 90 ? 'text-nebula-pink' : budgetUtilization > 75 ? 'text-stellar-gold' : 'text-aurora-green'}`}>
              {budgetUtilization.toFixed(1)}%
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        <div className="relative">
          {/* 3D Background Effect */}
          <div className="absolute inset-0 bg-gradient-to-br from-cosmic-purple/5 to-neon-cyan/5 rounded-lg blur-xl" />

          <ResponsiveContainer width="100%" height={400}>
            {renderChart()}
          </ResponsiveContainer>
        </div>

        {/* Legend */}
        <div className="flex flex-wrap justify-center gap-4 mt-6">
          {categories.map((category) => (
            <div 
              key={category.key}
              className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50 hover:bg-slate-700/50 transition-colors cursor-pointer"
            >
              <div 
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: category.color }}
              />
              <span className="text-sm font-space text-gray-300">{category.name}</span>
            </div>
          ))}
          <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
            <div className="w-4 h-1 bg-stellar-gold rounded" style={{ borderStyle: 'dashed' }} />
            <span className="text-sm font-space text-gray-300">Budget Line</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default EnhancedSpendingChart;

client/src/components/charts/FinancialGo.content:
import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line, Area, ComposedChart } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Target, Clock, TrendingUp, DollarSign, Calendar, Eye, BarChart3, PieChart as PieChartIcon } from 'lucide-react';
import { FinancialGoal } from '@/types/financial';

interface FinancialGoalsChartProps {
  goals: FinancialGoal[];
  monthlySavings: number;
  className?: string;
}

const FinancialGoalsChart: React.FC<FinancialGoalsChartProps> = ({
  goals,
  monthlySavings,
  className = ""
}) => {
  const [viewMode, setViewMode] = useState<'progress' | 'timeline' | 'priority'>('progress');

  const categoryColors: Record<string, string> = {
    emergency: '#EF4444',
    investment: '#8B5CF6', 
    purchase: '#F59E0B',
    retirement: '#10B981',
    education: '#3B82F6',
    other: '#6B7280'
  };

  const priorityColors: Record<string, string> = {
    high: '#EF4444',
    medium: '#F59E0B', 
    low: '#10B981'
  };

  // Prepare data for different chart views
  const progressData = goals.map(goal => ({
    ...goal,
    progress: (goal.currentAmount / goal.targetAmount) * 100,
    remaining: goal.targetAmount - goal.currentAmount,
    monthsToGoal: Math.ceil((goal.targetAmount - goal.currentAmount) / monthlySavings)
  }));

  const timelineData = goals.map((goal, index) => {
    const monthsToGoal = Math.ceil((goal.targetAmount - goal.currentAmount) / monthlySavings);
    const projectedData = [];

    for (let month = 0; month <= Math.min(monthsToGoal, 36); month++) {
      const projectedAmount = goal.currentAmount + (monthlySavings * month);
      projectedData.push({
        month: `Month ${month}`,
        [`goal_${index}`]: Math.min(projectedAmount, goal.targetAmount),
        target: goal.targetAmount,
        goalName: goal.description
      });
    }
    return projectedData;
  }).flat();

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="cosmic-card p-4 border border-neon-cyan/30 backdrop-blur-md">
          <p className="font-orbitron text-neon-cyan font-semibold mb-2">{label}</p>
          <div className="space-y-1">
            {payload.map((entry: any, index: number) => (
              <div key={index} className="flex items-center justify-between">
                <span className="text-gray-300 text-sm">{entry.dataKey}:</span>
                <span className="font-mono text-neon-cyan">₹{entry.value?.toLocaleString()}</span>
              </div>
            ))}
          </div>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    switch (viewMode) {
      case 'progress':
        return (
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={progressData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis 
                dataKey="description" 
                stroke="#E5E7EB"
                fontSize={12}
                tick={{ fill: '#E5E7EB' }}
                angle={-45}
                textAnchor="end"
                height={80}
              />
              <YAxis 
                stroke="#E5E7EB"
                fontSize={12}
                tick={{ fill: '#E5E7EB' }}
                tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
              />
              <Tooltip content={<CustomTooltip />} />
              <Bar 
                dataKey="currentAmount" 
                fill="#00F5FF" 
                name="Current Amount"
                radius={[4, 4, 0, 0]}
              />
              <Bar 
                dataKey="remaining" 
                fill="#374151" 
                name="Remaining Amount"
                radius={[4, 4, 0, 0]}
                opacity={0.3}
              />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'timeline':
        const uniqueMonths = [...new Set(timelineData.map(d => d.month))];
        const consolidatedTimeline = uniqueMonths.map(month => {
          const monthData = timelineData.filter(d => d.month === month);
          const result: any = { month };
          monthData.forEach((data, index) => {
            Object.keys(data).forEach(key => {
              if (key.startsWith('goal_')) {
                result[key] = data[key];
              }
            });
          });
          return result;
        });

        return (
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={consolidatedTimeline} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis 
                dataKey="month" 
                stroke="#E5E7EB"
                fontSize={12}
                tick={{ fill: '#E5E7EB' }}
              />
              <YAxis 
                stroke="#E5E7EB"
                fontSize={12}
                tick={{ fill: '#E5E7EB' }}
                tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
              />
              <Tooltip content={<CustomTooltip />} />
              {goals.map((goal, index) => (
                <Line
                  key={index}
                  type="monotone"
                  dataKey={`goal_${index}`}
                  stroke={Object.values(categoryColors)[index % Object.values(categoryColors).length]}
                  strokeWidth={3}
                  dot={{ fill: Object.values(categoryColors)[index % Object.values(categoryColors).length], r: 4 }}
                  name={goal.description}
                />
              ))}
            </LineChart>
          </ResponsiveContainer>
        );

      case 'priority':
        const priorityData = goals.map(goal => ({
          name: goal.description.substring(0, 20) + '...',
          value: goal.targetAmount,
          priority: goal.priority,
          progress: (goal.currentAmount / goal.targetAmount) * 100,
          category: goal.category
        }));

        return (
          <ResponsiveContainer width="100%" height={400}>
            <PieChart>
              <Pie
                data={priorityData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, value, progress }) => 
                  `${name}: ₹${(value / 1000).toFixed(0)}k (${progress.toFixed(0)}%)`
                }
                outerRadius={120}
                fill="#8884d8"
                dataKey="value"
              >
                {priorityData.map((entry, index) => (
                  <Cell 
                    key={`cell-${index}`} 
                    fill={priorityColors[entry.priority]} 
                  />
                ))}
              </Pie>
              <Tooltip 
                formatter={(value: number) => [`₹${value.toLocaleString()}`, 'Target Amount']}
              />
            </PieChart>
          </ResponsiveContainer>
        );

      default:
        return <div>Select a view mode</div>;
    }
  };

  // Calculate summary metrics
  const totalTargetAmount = goals.reduce((sum, goal) => sum + goal.targetAmount, 0);
  const totalCurrentAmount = goals.reduce((sum, goal) => sum + goal.currentAmount, 0);
  const overallProgress = totalTargetAmount > 0 ? (totalCurrentAmount / totalTargetAmount) * 100 : 0;
  const highPriorityGoals = goals.filter(goal => goal.priority === 'high').length;

  return (
    <Card className={`cosmic-card ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="font-orbitron text-xl text-cosmic-gradient flex items-center gap-2">
              <Target className="w-5 h-5 text-stellar-gold" />
              Financial Goals Tracker
            </CardTitle>
            <p className="text-sm text-gray-400 mt-1">Monitor progress towards your financial objectives</p>
          </div>

          <div className="flex gap-2">
            {[
              { mode: 'progress' as const, icon: BarChart3, label: 'Progress' },
              { mode: 'timeline' as const, icon: Clock, label: 'Timeline' },
              { mode: 'priority' as const, icon: PieChartIcon, label: 'Priority' },
            ].map(({ mode, icon: Icon, label }) => (
              <Button
                key={mode}
                size="sm"
                variant={viewMode === mode ? "default" : "outline"}
                onClick={() => setViewMode(mode)}
                className={`cosmic-button px-3 py-1 ${viewMode === mode ? 'bg-neon-cyan text-slate-900' : 'border-neon-cyan text-neon-cyan'}`}
              >
                <Icon className="w-4 h-4" />
              </Button>
            ))}
          </div>
        </div>

        {/* Summary Metrics */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mt-4">
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Total Goals</div>
            <div className="font-mono text-lg text-neon-cyan">{goals.length}</div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Overall Progress</div>
            <div className="font-mono text-lg text-cosmic-purple">{overallProgress.toFixed(1)}%</div>
            <Progress value={overallProgress} className="mt-1 h-1" />
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Target Amount</div>
            <div className="font-mono text-sm text-stellar-gold">₹{(totalTargetAmount / 100000).toFixed(1)}L</div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">High Priority</div>
            <div className="font-mono text-lg text-nebula-pink">{highPriorityGoals}</div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        {goals.length === 0 ? (
          <div className="text-center py-12">
            <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="font-orbitron text-lg text-gray-400 mb-2">No Financial Goals Set</h3>
            <p className="text-gray-500 text-sm">Add your financial goals to track progress and get insights</p>
          </div>
        ) : (
          <>
            {/* Chart */}
            <div className="mb-6">
              {renderChart()}
            </div>

            {/* Goals List */}
            <div className="space-y-4">
              <h4 className="font-orbitron text-lg text-neon-cyan mb-4">Goal Details</h4>
              {progressData.map((goal, index) => (
                <div key={goal.id} className="p-4 bg-gradient-to-r from-slate-800/50 to-slate-700/50 rounded-lg border border-slate-600">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center gap-3">
                      <div className={`w-3 h-3 rounded-full`} style={{ backgroundColor: categoryColors[goal.category] }} />
                      <h5 className="font-orbitron text-md text-cosmic-gradient">{goal.description}</h5>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge variant="outline" className={`border-${goal.priority === 'high' ? 'nebula-pink' : goal.priority === 'medium' ? 'stellar-gold' : 'aurora-green'} text-${goal.priority === 'high' ? 'nebula-pink' : goal.priority === 'medium' ? 'stellar-gold' : 'aurora-green'}`}>
                        {goal.priority.toUpperCase()}
                      </Badge>
                      <Badge variant="outline" className="border-neon-cyan text-neon-cyan">
                        {goal.category.toUpperCase()}
                      </Badge>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                    <div>
                      <div className="text-gray-400 mb-1">Current</div>
                      <div className="font-mono text-neon-cyan">₹{goal.currentAmount.toLocaleString()}</div>
                    </div>
                    <div>
                      <div className="text-gray-400 mb-1">Target</div>
                      <div className="font-mono text-stellar-gold">₹{goal.targetAmount.toLocaleString()}</div>
                    </div>
                    <div>
                      <div className="text-gray-400 mb-1">Progress</div>
                      <div className="font-mono text-cosmic-purple">{goal.progress.toFixed(1)}%</div>
                    </div>
                    <div>
                      <div className="text-gray-400 mb-1">Est. Time</div>
                      <div className="font-mono text-aurora-green">{goal.monthsToGoal}mo</div>
                    </div>
                  </div>

                  <Progress value={goal.progress} className="mt-3 h-2" />
                </div>
              ))}
            </div>
          </>
        )}
      </CardContent>
    </Card>
  );
};

export default FinancialGoalsChart;

client/src/components/charts/GoalTimelin.content:
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, ReferenceLine, Area, ComposedChart } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Target, Calendar, TrendingUp, Zap, Clock, CheckCircle } from 'lucide-react';

interface GoalTimelineData {
  month: string;
  currentProgress: number;
  projectedProgress: number;
  simulatedProgress: number;
  goalTarget: number;
  milestone?: string;
  achievementDate?: string;
}

interface GoalTimelineChartProps {
  data: GoalTimelineData[];
  goalName: string;
  targetAmount: number;
  currentAmount: number;
  projectedDate: string;
  simulatedDate?: string;
  isSimulating?: boolean;
  className?: string;
}

const GoalTimelineChart: React.FC<GoalTimelineChartProps> = ({
  data,
  goalName,
  targetAmount,
  currentAmount,
  projectedDate,
  simulatedDate,
  isSimulating = false,
  className = ""
}) => {
  const [viewMode, setViewMode] = useState<'timeline' | 'progress' | 'comparison'>('timeline');
  const [highlightedMilestone, setHighlightedMilestone] = useState<string | null>(null);
  const [animationFrame, setAnimationFrame] = useState(0);

  useEffect(() => {
    if (isSimulating) {
      const timer = setInterval(() => {
        setAnimationFrame(prev => (prev + 1) % 360);
      }, 100);
      return () => clearInterval(timer);
    }
  }, [isSimulating]);

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="cosmic-card p-4 border border-neon-cyan/30 backdrop-blur-md">
          <p className="font-orbitron text-neon-cyan font-semibold mb-2">{label}</p>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Current:</span>
              <span className="font-mono text-neon-cyan">₹{data.currentProgress?.toLocaleString()}</span>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Projected:</span>
              <span className="font-mono text-cosmic-purple">₹{data.projectedProgress?.toLocaleString()}</span>
            </div>

            {data.simulatedProgress && (
              <div className="flex items-center justify-between">
                <span className="text-gray-300 text-sm">Simulated:</span>
                <span className="font-mono text-aurora-green">₹{data.simulatedProgress?.toLocaleString()}</span>
              </div>
            )}

            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Target:</span>
              <span className="font-mono text-stellar-gold">₹{data.goalTarget?.toLocaleString()}</span>
            </div>

            {data.milestone && (
              <div className="border-t border-gray-600 pt-2">
                <p className="text-xs text-aurora-green">🎯 {data.milestone}</p>
              </div>
            )}
          </div>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    switch (viewMode) {
      case 'timeline':
        return (
          <ComposedChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />

            {/* Goal Target Line */}
            <ReferenceLine 
              y={targetAmount} 
              stroke="#FFD700" 
              strokeDasharray="8 4" 
              strokeWidth={2}
              label={{ value: "Goal Target", position: "top", fill: "#FFD700" }}
            />

            {/* Current Progress */}
            <Line 
              type="monotone" 
              dataKey="currentProgress" 
              stroke="#00F5FF" 
              strokeWidth={3}
              dot={{ fill: '#00F5FF', strokeWidth: 2, r: 4 }}
              name="Current Progress"
            />

            {/* Projected Progress */}
            <Line 
              type="monotone" 
              dataKey="projectedProgress" 
              stroke="#8B5CF6" 
              strokeWidth={2}
              strokeDasharray="5 5"
              dot={{ fill: '#8B5CF6', strokeWidth: 2, r: 3 }}
              name="Projected Progress"
            />

            {/* Simulated Progress with Animation */}
            {data.some(d => d.simulatedProgress) && (
              <Line 
                type="monotone" 
                dataKey="simulatedProgress" 
                stroke="#00FF7F" 
                strokeWidth={3}
                dot={{ 
                  fill: '#00FF7F', 
                  strokeWidth: 2, 
                  r: isSimulating ? 5 : 4,
                  style: {
                    filter: isSimulating ? 'drop-shadow(0 0 8px #00FF7F)' : 'none'
                  }
                }}
                name="Simulated Progress"
                animationDuration={1500}
              />
            )}
          </ComposedChart>
        );

      case 'progress':
        const progressData = data.map(item => ({
          ...item,
          currentPercent: (item.currentProgress / targetAmount) * 100,
          projectedPercent: (item.projectedProgress / targetAmount) * 100,
          simulatedPercent: item.simulatedProgress ? (item.simulatedProgress / targetAmount) * 100 : 0
        }));

        return (
          <ComposedChart data={progressData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `${value}%`}
              domain={[0, 120]}
            />
            <Tooltip 
              formatter={(value: number, name: string) => [
                `${value.toFixed(1)}%`,
                name.replace('Percent', ' Progress')
              ]}
            />

            <ReferenceLine y={100} stroke="#FFD700" strokeDasharray="8 4" strokeWidth={2} />

            <Area
              type="monotone"
              dataKey="currentPercent"
              stroke="#00F5FF"
              fill="url(#currentGradient)"
              strokeWidth={2}
            />

            <Line 
              type="monotone" 
              dataKey="projectedPercent" 
              stroke="#8B5CF6" 
              strokeWidth={2}
              strokeDasharray="5 5"
              dot={{ fill: '#8B5CF6', strokeWidth: 2, r: 3 }}
            />

            {data.some(d => d.simulatedProgress) && (
              <Line 
                type="monotone" 
                dataKey="simulatedPercent" 
                stroke="#00FF7F" 
                strokeWidth={3}
                dot={{ fill: '#00FF7F', strokeWidth: 2, r: 4 }}
              />
            )}

            <defs>
              <linearGradient id="currentGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#00F5FF" stopOpacity={0.8}/>
                <stop offset="95%" stopColor="#00F5FF" stopOpacity={0.1}/>
              </linearGradient>
            </defs>
          </ComposedChart>
        );

      case 'comparison':
        const comparisonData = data.map(item => ({
          ...item,
          timeSaved: item.simulatedProgress && item.projectedProgress 
            ? Math.max(0, item.simulatedProgress - item.projectedProgress)
            : 0
        }));

        return (
          <ComposedChart data={comparisonData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />

            <Area
              type="monotone"
              dataKey="timeSaved"
              stroke="#00FF7F"
              fill="url(#improvementGradient)"
              strokeWidth={2}
            />

            <defs>
              <linearGradient id="improvementGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#00FF7F" stopOpacity={0.8}/>
                <stop offset="95%" stopColor="#00FF7F" stopOpacity={0.1}/>
              </linearGradient>
            </defs>
          </ComposedChart>
        );

      default:
        return <div>Invalid view mode</div>;
    }
  };

  // Calculate key metrics
  const currentProgress = (currentAmount / targetAmount) * 100;
  const remainingAmount = targetAmount - currentAmount;
  const monthsToGoal = simulatedDate 
    ? new Date(simulatedDate).getMonth() - new Date().getMonth()
    : new Date(projectedDate).getMonth() - new Date().getMonth();
  const timeSaved = simulatedDate 
    ? (new Date(projectedDate).getTime() - new Date(simulatedDate).getTime()) / (1000 * 60 * 60 * 24 * 30)
    : 0;

  return (
    <Card className={`cosmic-card transform-3d transition-all duration-500 hover:scale-105 ${className} ${isSimulating ? 'animate-glow' : ''}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="font-orbitron text-xl text-cosmic-gradient flex items-center gap-2">
              <Target className="w-5 h-5 text-stellar-gold" />
              {goalName} Timeline
              {isSimulating && <Zap className="w-4 h-4 text-stellar-gold animate-pulse" />}
            </CardTitle>
            <p className="text-sm text-gray-400 mt-1">Track your progress towards financial goals</p>
          </div>

          <div className="flex gap-2">
            {[
              { mode: 'timeline' as const, icon: Calendar, label: 'Timeline' },
              { mode: 'progress' as const, icon: TrendingUp, label: 'Progress' },
              { mode: 'comparison' as const, icon: CheckCircle, label: 'Compare' },
            ].map(({ mode, icon: Icon, label }) => (
              <Button
                key={mode}
                size="sm"
                variant={viewMode === mode ? "default" : "outline"}
                onClick={() => setViewMode(mode)}
                className={`cosmic-button px-3 py-1 ${viewMode === mode ? 'bg-neon-cyan text-slate-900' : 'border-neon-cyan text-neon-cyan'}`}
              >
                <Icon className="w-4 h-4" />
              </Button>
            ))}
          </div>
        </div>

        {/* Goal Status */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mt-4">
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Current Progress</div>
            <div className="font-mono text-lg text-neon-cyan">{currentProgress.toFixed(1)}%</div>
            <div className="text-xs text-gray-400">₹{currentAmount.toLocaleString()}</div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Remaining</div>
            <div className="font-mono text-lg text-cosmic-purple">₹{remainingAmount.toLocaleString()}</div>
            <div className="text-xs text-gray-400">{(100 - currentProgress).toFixed(1)}% left</div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Target Date</div>
            <div className="font-mono text-sm text-stellar-gold">
              {simulatedDate ? new Date(simulatedDate).toLocaleDateString() : new Date(projectedDate).toLocaleDateString()}
            </div>
            <div className="text-xs text-gray-400">{Math.abs(monthsToGoal)} months</div>
          </div>

          {timeSaved > 0 && (
            <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
              <div className="text-xs text-gray-400 mb-1">Time Saved</div>
