import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';
import { Target, Clock, TrendingUp, DollarSign, Star } from 'lucide-react';

interface FinancialGoal {
  id: string;
  description: string;
  targetAmount: number;
  currentAmount: number;
  priority: 'high' | 'medium' | 'low';
  category: 'emergency' | 'investment' | 'purchase' | 'retirement' | 'education' | 'other';
}

interface IndividualGoalChartProps {
  goal: FinancialGoal;
  monthlySavings: number;
  goalIndex: number;
  className?: string;
}

const IndividualGoalChart: React.FC<IndividualGoalChartProps> = ({
  goal,
  monthlySavings,
  goalIndex,
  className = ""
}) => {
  const priorityColors = {
    high: '#ef4444',
    medium: '#f59e0b',
    low: '#10b981'
  };

  const categoryColors = {
    emergency: '#ef4444',
    investment: '#8b5cf6',
    purchase: '#f59e0b',
    retirement: '#10b981',
    education: '#3b82f6',
    other: '#6b7280'
  };

  // Calculate timeline data
  const remainingAmount = goal.targetAmount - goal.currentAmount;
  const monthsToGoal = Math.ceil(remainingAmount / Math.max(monthlySavings, 1000));
  const progressPercentage = (goal.currentAmount / goal.targetAmount) * 100;

  // Generate projection data for the chart
  const projectionData = [];
  for (let month = 0; month <= Math.min(monthsToGoal, 60); month++) {
    const projectedAmount = Math.min(
      goal.currentAmount + (monthlySavings * month),
      goal.targetAmount
    );

    projectionData.push({
      month: `Month ${month}`,
      monthNumber: month,
      current: month === 0 ? goal.currentAmount : 0,
      projected: projectedAmount,
      target: goal.targetAmount,
      progress: (projectedAmount / goal.targetAmount) * 100
    });
  }

  // Milestone markers
  interface Milestone {
    month: number;
    percentage: number;
    amount: number;
  }

  const milestones: Milestone[] = [];
  const milestoneIntervals = [25, 50, 75, 90, 100];
  milestoneIntervals.forEach(percentage => {
    const milestoneAmount = (goal.targetAmount * percentage) / 100;
    const monthsToMilestone = Math.ceil((milestoneAmount - goal.currentAmount) / monthlySavings);
    if (monthsToMilestone > 0 && monthsToMilestone <= monthsToGoal) {
      milestones.push({
        month: monthsToMilestone,
        percentage,
        amount: milestoneAmount
      });
    }
  });

  return (
    <Card className={`cosmic-card ${className}`}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-orbitron text-cosmic-gradient flex items-center gap-2">
            <Target className="w-5 h-5" />
            Goal {goalIndex + 1}: {goal.description}
          </CardTitle>
          <div className="flex items-center gap-2">
            <Badge 
              variant="outline" 
              className="border-2"
              style={{ 
                borderColor: priorityColors[goal.priority], 
                color: priorityColors[goal.priority] 
              }}
            >
              <Star className="w-3 h-3 mr-1" />
              {goal.priority.toUpperCase()}
            </Badge>
            <Badge 
              variant="outline"
              style={{ 
                borderColor: categoryColors[goal.category], 
                color: categoryColors[goal.category] 
              }}
            >
              {goal.category.toUpperCase()}
            </Badge>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Goal Overview */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="cosmic-card-inner p-3 text-center">
            <div className="text-xl font-bold text-neon-cyan">
              ₹{goal.targetAmount.toLocaleString()}
            </div>
            <div className="text-xs text-gray-400">Target Amount</div>
          </div>

          <div className="cosmic-card-inner p-3 text-center">
            <div className="text-xl font-bold text-stellar-gold">
              ₹{goal.currentAmount.toLocaleString()}
            </div>
            <div className="text-xs text-gray-400">Current Amount</div>
          </div>

          <div className="cosmic-card-inner p-3 text-center">
            <div className="text-xl font-bold text-nebula-pink">
              {monthsToGoal} months
            </div>
            <div className="text-xs text-gray-400">Time to Goal</div>
          </div>

          <div className="cosmic-card-inner p-3 text-center">
            <div className="text-xl font-bold text-neon-green">
              {progressPercentage.toFixed(1)}%
            </div>
            <div className="text-xs text-gray-400">Progress</div>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="space-y-2">
          <div className="flex justify-between text-sm">
            <span className="text-gray-400">Progress to Goal</span>
            <span className="text-neon-cyan font-medium">{progressPercentage.toFixed(1)}%</span>
          </div>
          <Progress 
            value={progressPercentage} 
            className="h-3"
            style={{ 
              background: `linear-gradient(to right, ${categoryColors[goal.category]} 0%, transparent 100%)` 
            }}
          />
          <div className="flex justify-between text-xs text-gray-500">
            <span>₹{goal.currentAmount.toLocaleString()}</span>
            <span>₹{goal.targetAmount.toLocaleString()}</span>
          </div>
        </div>

        {/* Timeline Chart */}
        <div className="space-y-3">
          <h4 className="text-sm font-orbitron text-neon-cyan flex items-center gap-2">
            <TrendingUp className="w-4 h-4" />
            Goal Achievement Timeline
          </h4>

          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart data={projectionData}>
                <defs>
                  <linearGradient id={`goalGradient${goalIndex}`} x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor={categoryColors[goal.category]} stopOpacity={0.3}/>
                    <stop offset="95%" stopColor={categoryColors[goal.category]} stopOpacity={0.05}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis 
                  dataKey="monthNumber" 
                  stroke="#9CA3AF"
                  fontSize={12}
                  tickFormatter={(value) => `M${value}`}
                />
                <YAxis 
                  stroke="#9CA3AF"
                  fontSize={12}
                  tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}K`}
                />
                <Tooltip
                  contentStyle={{
                    backgroundColor: '#1F2937',
                    border: '1px solid #374151',
                    borderRadius: '8px',
                    color: '#F3F4F6'
                  }}
                  formatter={(value: any, name: string) => [
                    `₹${value.toLocaleString()}`,
                    name === 'projected' ? 'Projected Amount' : 
                    name === 'target' ? 'Target Amount' : 'Current Amount'
                  ]}
                  labelFormatter={(label) => `Month ${label}`}
                />
                <Area
                  type="monotone"
                  dataKey="projected"
                  stroke={categoryColors[goal.category]}
                  strokeWidth={2}
                  fill={`url(#goalGradient${goalIndex})`}
                />
                <Line
                  type="monotone"
                  dataKey="target"
                  stroke="#ef4444"
                  strokeWidth={2}
                  strokeDasharray="5 5"
                  dot={false}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Milestones */}
        {milestones.length > 0 && (
          <div className="space-y-3">
            <h4 className="text-sm font-orbitron text-neon-cyan flex items-center gap-2">
              <Clock className="w-4 h-4" />
              Achievement Milestones
            </h4>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {milestones.map((milestone, index) => (
                <div key={index} className="cosmic-card-inner p-3 flex items-center justify-between">
                  <div>
                    <div className="text-sm font-medium text-white">
                      {milestone.percentage}% Complete
                    </div>
                    <div className="text-xs text-gray-400">
                      ₹{milestone.amount.toLocaleString()}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium text-neon-cyan">
                      Month {milestone.month}
                    </div>
                    <div className="text-xs text-gray-400">
                      {new Date(Date.now() + milestone.month * 30 * 24 * 60 * 60 * 1000).toLocaleDateString()}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}


      </CardContent>
    </Card>
  );
};

export default IndividualGoalChart;

client/src/components/charts/LiveTransfo.content:
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, ReferenceLine, Area, ComposedChart, AreaChart } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Zap, TrendingUp, Target, RefreshCw, Eye } from 'lucide-react';

interface TransformationData {
  month: string;
  beforeScenario: number;
  afterScenario: number;
  goalTarget: number;
  savings: number;
  difference: number;
}

interface LiveTransformationChartProps {
  data: TransformationData[];
  title: string;
  scenario: string;
  isLive?: boolean;
  className?: string;
}

const LiveTransformationChart: React.FC<LiveTransformationChartProps> = ({
  data,
  title,
  scenario,
  isLive = false,
  className = ""
}) => {
  const [viewMode, setViewMode] = useState<'comparison' | 'difference' | 'progress'>('comparison');
  const [animationProgress, setAnimationProgress] = useState(0);
  const [showPrediction, setShowPrediction] = useState(true);

  useEffect(() => {
    if (isLive) {
      const timer = setInterval(() => {
        setAnimationProgress(prev => (prev + 1) % 100);
      }, 50);
      return () => clearInterval(timer);
    }
  }, [isLive]);

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="cosmic-card p-4 border border-neon-cyan/30 backdrop-blur-md">
          <p className="font-orbitron text-neon-cyan font-semibold mb-2">{label}</p>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Before:</span>
              <span className="font-mono text-nebula-pink">₹{data.beforeScenario?.toLocaleString()}</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">After:</span>
              <span className="font-mono text-aurora-green">₹{data.afterScenario?.toLocaleString()}</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Target:</span>
              <span className="font-mono text-stellar-gold">₹{data.goalTarget?.toLocaleString()}</span>
            </div>
            <div className="border-t border-gray-600 pt-2">
              <div className="flex items-center justify-between">
                <span className="text-gray-300 text-sm">Improvement:</span>
                <span className={`font-mono ${data.difference > 0 ? 'text-aurora-green' : 'text-nebula-pink'}`}>
                  {data.difference > 0 ? '+' : ''}₹{data.difference?.toLocaleString()}
                </span>
              </div>
            </div>
          </div>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    switch (viewMode) {
      case 'comparison':
        return (
          <ComposedChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />

            {/* Goal Target Line */}
            <Line 
              type="monotone" 
              dataKey="goalTarget" 
              stroke="#FFD700" 
              strokeWidth={2}
              strokeDasharray="8 4"
              dot={{ fill: '#FFD700', strokeWidth: 2, r: 3 }}
              name="Goal Target"
            />

            {/* Before Scenario */}
            <Line 
              type="monotone" 
              dataKey="beforeScenario" 
              stroke="#FF1493" 
              strokeWidth={3}
              dot={{ fill: '#FF1493', strokeWidth: 2, r: 4 }}
              name="Before Scenario"
              strokeDasharray={isLive ? "5 5" : "none"}
            />

            {/* After Scenario with Animation */}
            <Line 
              type="monotone" 
              dataKey="afterScenario" 
              stroke="#00FF7F" 
              strokeWidth={3}
              dot={{ fill: '#00FF7F', strokeWidth: 2, r: 4 }}
              name="After Scenario"
              animationDuration={2000}
              style={{
                filter: isLive ? 'drop-shadow(0 0 8px #00FF7F)' : 'none'
              }}
            />
          </ComposedChart>
        );

      case 'difference':
        return (
          <AreaChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `₹${(value / 1000).toFixed(0)}k`}
            />
            <Tooltip content={<CustomTooltip />} />

            <Area
              type="monotone"
              dataKey="difference"
              stroke="#00F5FF"
              fill="url(#differenceGradient)"
              strokeWidth={2}
            />

            <defs>
              <linearGradient id="differenceGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#00F5FF" stopOpacity={0.8}/>
                <stop offset="95%" stopColor="#00F5FF" stopOpacity={0.1}/>
              </linearGradient>
            </defs>

            <ReferenceLine y={0} stroke="#666" strokeDasharray="2 2" />
          </AreaChart>
        );

      case 'progress':
        return (
          <ComposedChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="month" 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
            />
            <YAxis 
              stroke="#E5E7EB"
              fontSize={12}
              tick={{ fill: '#E5E7EB' }}
              tickFormatter={(value) => `${value}%`}
            />
            <Tooltip 
              formatter={(value: number, name: string) => [
                `${value.toFixed(1)}%`,
                name === 'progressBefore' ? 'Progress Before' : 'Progress After'
              ]}
            />

            <Area
              type="monotone"
              dataKey="progressBefore"
              stackId="1"
              stroke="#FF1493"
              fill="#FF1493"
              fillOpacity={0.6}
            />

            <Area
              type="monotone"
              dataKey="progressAfter"
              stackId="2"
              stroke="#00FF7F"
              fill="#00FF7F"
              fillOpacity={0.8}
            />
          </ComposedChart>
        );

      default:
        return <div>Invalid view mode</div>;
    }
  };

  // Calculate key metrics
  const totalImprovement = data.reduce((sum, item) => sum + item.difference, 0);
  const avgImprovement = totalImprovement / data.length;
  const maxImprovement = Math.max(...data.map(item => item.difference));
  const goalAchievementImprovement = data.reduce((acc, item) => {
    const beforeGap = Math.max(0, item.goalTarget - item.beforeScenario);
    const afterGap = Math.max(0, item.goalTarget - item.afterScenario);
    return acc + (beforeGap - afterGap);
  }, 0);

  return (
    <Card className={`cosmic-card transform-3d transition-all duration-500 hover:scale-105 ${className} ${isLive ? 'animate-glow' : ''}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="font-orbitron text-xl text-cosmic-gradient flex items-center gap-2">
              {isLive && <Zap className="w-5 h-5 text-stellar-gold animate-pulse" />}
              {title}
            </CardTitle>
            <p className="text-sm text-gray-400 mt-1">Scenario: {scenario}</p>
          </div>

          <div className="flex gap-2">
            {[
              { mode: 'comparison' as const, icon: TrendingUp, label: 'Compare' },
              { mode: 'difference' as const, icon: Target, label: 'Impact' },
              { mode: 'progress' as const, icon: Eye, label: 'Progress' },
            ].map(({ mode, icon: Icon, label }) => (
              <Button
                key={mode}
                size="sm"
                variant={viewMode === mode ? "default" : "outline"}
                onClick={() => setViewMode(mode)}
                className={`cosmic-button px-3 py-1 ${viewMode === mode ? 'bg-neon-cyan text-slate-900' : 'border-neon-cyan text-neon-cyan'}`}
              >
                <Icon className="w-4 h-4" />
              </Button>
            ))}
          </div>
        </div>

        {/* Live Metrics */}
        <div className="grid grid-cols-4 gap-3 mt-4">
          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Total Impact</div>
            <div className={`font-mono text-sm ${totalImprovement > 0 ? 'text-aurora-green' : 'text-nebula-pink'}`}>
              {totalImprovement > 0 ? '+' : ''}₹{totalImprovement.toLocaleString()}
            </div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Avg Monthly</div>
            <div className={`font-mono text-sm ${avgImprovement > 0 ? 'text-aurora-green' : 'text-nebula-pink'}`}>
              {avgImprovement > 0 ? '+' : ''}₹{avgImprovement.toLocaleString()}
            </div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Peak Impact</div>
            <div className={`font-mono text-sm ${maxImprovement > 0 ? 'text-aurora-green' : 'text-nebula-pink'}`}>
              {maxImprovement > 0 ? '+' : ''}₹{maxImprovement.toLocaleString()}
            </div>
          </div>

          <div className="text-center p-3 rounded-lg bg-gradient-to-br from-slate-800/50 to-slate-700/50">
            <div className="text-xs text-gray-400 mb-1">Goal Progress</div>
            <div className={`font-mono text-sm ${goalAchievementImprovement > 0 ? 'text-aurora-green' : 'text-nebula-pink'}`}>
              {goalAchievementImprovement > 0 ? '+' : ''}₹{goalAchievementImprovement.toLocaleString()}
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        <div className="relative">
          {/* Live Animation Overlay */}
          {isLive && (
            <div className="absolute top-0 right-0 z-10">
              <div className="flex items-center gap-2 bg-stellar-gold/20 px-3 py-1 rounded-full">
                <RefreshCw className="w-4 h-4 text-stellar-gold animate-spin" />
                <span className="text-xs font-mono text-stellar-gold">LIVE</span>
              </div>
            </div>
          )}

          <ResponsiveContainer width="100%" height={400}>
            {renderChart()}
          </ResponsiveContainer>
        </div>

        {/* Legend */}
        <div className="flex flex-wrap justify-center gap-4 mt-6">
          {viewMode === 'comparison' && (
            <>
              <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
                <div className="w-4 h-1 bg-nebula-pink rounded" />
                <span className="text-sm font-space text-gray-300">Before Scenario</span>
              </div>
              <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
                <div className="w-4 h-1 bg-aurora-green rounded" />
                <span className="text-sm font-space text-gray-300">After Scenario</span>
              </div>
              <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
                <div className="w-4 h-1 bg-stellar-gold rounded" style={{ borderStyle: 'dashed' }} />
                <span className="text-sm font-space text-gray-300">Goal Target</span>
              </div>
            </>
          )}

          {viewMode === 'difference' && (
            <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
              <div className="w-4 h-4 bg-gradient-to-b from-neon-cyan to-transparent rounded" />
              <span className="text-sm font-space text-gray-300">Financial Impact</span>
            </div>
          )}

          {viewMode === 'progress' && (
            <>
              <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
                <div className="w-4 h-4 bg-nebula-pink/60 rounded" />
                <span className="text-sm font-space text-gray-300">Progress Before</span>
              </div>
              <div className="flex items-center gap-2 p-2 rounded-lg bg-slate-800/50">
                <div className="w-4 h-4 bg-aurora-green/80 rounded" />
                <span className="text-sm font-space text-gray-300">Progress After</span>
              </div>
            </>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default LiveTransformationChart;

client/src/components/charts/NeedWantCha.content:
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';
import { useState } from 'react';

interface NeedWantChartProps {
  needsPercentage: number;
  wantsPercentage: number;
}

export function NeedWantChart({ needsPercentage, wantsPercentage }: NeedWantChartProps) {
  const [activeIndex, setActiveIndex] = useState<number | null>(null);

  const data = [
    { 
      name: 'Essential Needs', 
      value: needsPercentage, 
      color: '#10B981',
      description: 'Housing, Food, Transportation'
    },
    { 
      name: 'Lifestyle Wants', 
      value: wantsPercentage, 
      color: '#EF4444',
      description: 'Entertainment, Shopping, Subscriptions'
    },
  ];

  const RADIAN = Math.PI / 180;
  const renderCustomizedLabel = ({
    cx, cy, midAngle, innerRadius, outerRadius, percent, name
  }: any) => {
    const radius = innerRadius + (outerRadius - innerRadius) * 0.6;
    const x = cx + radius * Math.cos(-midAngle * RADIAN);
    const y = cy + radius * Math.sin(-midAngle * RADIAN);

    return (
      <g>
        <text 
          x={x} 
          y={y - 10} 
          fill="white" 
          textAnchor={x > cx ? 'start' : 'end'} 
          dominantBaseline="central"
          fontSize={14}
          fontWeight="bold"
          className="drop-shadow-lg"
        >
          {name.split(' ')[0]}
        </text>
        <text 
          x={x} 
          y={y + 10} 
          fill="#FFD700" 
          textAnchor={x > cx ? 'start' : 'end'} 
          dominantBaseline="central"
          fontSize={16}
          fontWeight="bold"
          className="drop-shadow-lg"
        >
          {`${(percent * 100).toFixed(0)}%`}
        </text>
      </g>
    );
  };

  const onPieEnter = (_: any, index: number) => {
    setActiveIndex(index);
  };

  const onPieLeave = () => {
    setActiveIndex(null);
  };

  return (
    <div className="w-full h-96">
      <h3 className="font-orbitron text-xl font-bold mb-4 text-center text-cosmic-gradient flex items-center justify-center">
        <span className="mr-2">⚖️</span>
        Needs vs Wants Analysis
      </h3>
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={data}
            cx="50%"
            cy="50%"
            labelLine={false}
            label={renderCustomizedLabel}
            outerRadius={activeIndex !== null ? 120 : 110}
            innerRadius={40}
            fill="#8884d8"
            dataKey="value"
            stroke="#1A1A3A"
            strokeWidth={4}
            onMouseEnter={onPieEnter}
            onMouseLeave={onPieLeave}
          >
            {data.map((entry, index) => (
              <Cell 
                key={`cell-${index}`} 
                fill={entry.color}
                stroke={activeIndex === index ? '#FFD700' : entry.color}
                strokeWidth={activeIndex === index ? 5 : 3}
                style={{
                  filter: activeIndex === index ? 'brightness(1.3) drop-shadow(0 0 15px rgba(255, 215, 0, 0.6))' : 'none',
                  transition: 'all 0.3s ease'
                }}
              />
            ))}
          </Pie>
          <Tooltip
            contentStyle={{
              backgroundColor: 'rgba(26, 26, 58, 0.95)',
              border: '2px solid rgba(0, 245, 255, 0.5)',
              borderRadius: '12px',
              color: '#ffffff',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
              backdropFilter: 'blur(10px)'
            }}
            formatter={(value: number, name: string, props: any) => [
              <div key="tooltip-content">
                <div className="font-mono text-stellar-gold text-lg font-bold">{value}%</div>
                <div className="text-gray-300 text-sm mt-1">{props.payload.description}</div>
              </div>,
              <span className="font-orbitron text-neon-cyan font-bold">{name}</span>
            ]}
          />
          <Legend
            wrapperStyle={{ 
              color: '#ffffff', 
              fontSize: '14px',
              paddingTop: '20px'
            }}
            iconType="circle"
            formatter={(value, entry: any) => (
              <span style={{ color: entry.color, fontWeight: 'bold', fontSize: '14px' }}>
                {value} ({entry.payload.value}%)
              </span>
            )}
          />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
}

client/src/components/charts/OverallGoal.content:
import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend, PieChart, Pie, Cell } from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Target, Clock, Users, TrendingUp, PieChart as PieChartIcon } from 'lucide-react';

interface FinancialGoal {
  id: string;
  description: string;
  targetAmount: number;
  currentAmount: number;
  priority: 'high' | 'medium' | 'low';
  category: 'emergency' | 'investment' | 'purchase' | 'retirement' | 'education' | 'other';
}

interface OverallGoalsTimelineProps {
  goals: FinancialGoal[];
  monthlySavings: number;
  className?: string;
}

const OverallGoalsTimeline: React.FC<OverallGoalsTimelineProps> = ({
  goals,
  monthlySavings,
  className = ""
}) => {
  const [showPieChart, setShowPieChart] = useState(false);
  const categoryColors = {
    emergency: '#ef4444',
    investment: '#8b5cf6',
    purchase: '#f59e0b',
    retirement: '#10b981',
    education: '#3b82f6',
    other: '#6b7280'
  };

  const priorityColors = {
    high: '#ef4444',
    medium: '#f59e0b',
    low: '#10b981'
  };

  // Sort goals by priority for achievement order
  const sortedGoals = [...goals].sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });

  // Calculate timeline data for all goals
  const timelineData = [];
  const maxMonths = 120; // 10 years max view

  // Calculate when each goal will be achieved
  const goalAchievementData = sortedGoals.map((goal, index) => {
    const remainingAmount = goal.targetAmount - goal.currentAmount;
    const monthsToGoal = Math.ceil(remainingAmount / Math.max(monthlySavings, 1000));

    return {
      goal,
      index,
      monthsToGoal,
      achievementMonth: monthsToGoal,
      cumulativeTarget: sortedGoals.slice(0, index + 1).reduce((sum, g) => sum + g.targetAmount, 0)
    };
  });

  // Generate monthly data for the chart
  for (let month = 0; month <= Math.min(maxMonths, Math.max(...goalAchievementData.map(g => g.monthsToGoal))); month++) {
    const monthData: any = {
      month: `Month ${month}`,
      monthNumber: month,
    };

    // Add cumulative progress line
    let cumulativeProgress = 0;
    goalAchievementData.forEach(({ goal, monthsToGoal, index }) => {
      const goalProgress = month <= monthsToGoal 
        ? goal.currentAmount + (monthlySavings * month)
        : goal.targetAmount;

      if (month <= monthsToGoal) {
        cumulativeProgress += Math.min(goalProgress, goal.targetAmount);
      } else {
        cumulativeProgress += goal.targetAmount;
      }

      // Individual goal lines
      monthData[`goal_${index}`] = month <= monthsToGoal ? goalProgress : goal.targetAmount;
      monthData[`goal_${index}_name`] = goal.description;
      monthData[`goal_${index}_color`] = categoryColors[goal.category];
    });

    monthData.cumulativeProgress = cumulativeProgress;
    monthData.totalTarget = goals.reduce((sum, goal) => sum + goal.targetAmount, 0);

    timelineData.push(monthData);
  }

  // Calculate achievement milestones
  const achievements = goalAchievementData.map(({ goal, monthsToGoal, cumulativeTarget }) => ({
    month: monthsToGoal,
    goalName: goal.description,
    amount: goal.targetAmount,
    cumulativeAmount: cumulativeTarget,
    priority: goal.priority,
    category: goal.category,
    date: new Date(Date.now() + monthsToGoal * 30 * 24 * 60 * 60 * 1000).toLocaleDateString()
  })).sort((a, b) => a.month - b.month);

  return (
    <div className={`space-y-6 ${className}`}>
      <Card className="cosmic-card">
        <CardHeader>
          <CardTitle className="font-orbitron text-xl text-cosmic-gradient flex items-center gap-2">
            <Users className="w-5 h-5 text-stellar-gold" />
            Overall Goals Achievement Timeline
          </CardTitle>
          <p className="text-sm text-gray-400">
            Complete roadmap showing when each financial goal will be achieved
          </p>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Goals Summary */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="cosmic-card-inner p-4 text-center">
              <div className="text-2xl font-bold text-neon-cyan">
                {goals.length}
              </div>
              <div className="text-sm text-gray-400">Total Goals</div>
            </div>

            <div className="cosmic-card-inner p-4 text-center">
              <div className="text-2xl font-bold text-stellar-gold">
                ₹{goals.reduce((sum, goal) => sum + goal.targetAmount, 0).toLocaleString()}
              </div>
              <div className="text-sm text-gray-400">Total Target</div>
            </div>

            <div className="cosmic-card-inner p-4 text-center">
              <div className="text-2xl font-bold text-nebula-pink">
                {Math.max(...goalAchievementData.map(g => g.monthsToGoal))} months
              </div>
              <div className="text-sm text-gray-400">Last Goal</div>
            </div>

            <div className="cosmic-card-inner p-4 text-center">
              <div className="text-2xl font-bold text-neon-green">
                ₹{monthlySavings.toLocaleString()}
              </div>
              <div className="text-sm text-gray-400">Monthly Savings</div>
            </div>
          </div>

          {/* Timeline Chart */}
          <div className="space-y-3">
            <h4 className="text-lg font-orbitron text-neon-cyan flex items-center gap-2">
              <TrendingUp className="w-5 h-5" />
              Cumulative Goals Achievement
            </h4>

            <div className="h-80">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={timelineData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                  <XAxis 
                    dataKey="monthNumber" 
                    stroke="#9CA3AF"
                    fontSize={12}
                    tickFormatter={(value) => `M${value}`}
                  />
                  <YAxis 
                    stroke="#9CA3AF"
                    fontSize={12}
                    tickFormatter={(value) => `₹${(value / 100000).toFixed(0)}L`}
                  />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: '#1F2937',
                      border: '1px solid #374151',
                      borderRadius: '8px',
                      color: '#F3F4F6'
                    }}
                    formatter={(value: any, name: string) => {
                      if (name === 'cumulativeProgress') {
                        return [`₹${value.toLocaleString()}`, 'Cumulative Progress'];
                      }
                      const goalIndex = name.split('_')[1];
                      const goalName = timelineData[0][`goal_${goalIndex}_name`];
                      return [`₹${value.toLocaleString()}`, goalName];
                    }}
                    labelFormatter={(label) => `Month ${label}`}
                  />
                  <Legend />

                  {/* Individual goal lines */}
                  {sortedGoals.map((goal, index) => (
                    <Line
                      key={`goal_${index}`}
                      type="monotone"
                      dataKey={`goal_${index}`}
                      stroke={categoryColors[goal.category]}
                      strokeWidth={2}
                      strokeDasharray="2 2"
                      dot={false}
                      name={goal.description}
                    />
                  ))}

                  {/* Cumulative progress line */}
                  <Line
                    type="monotone"
                    dataKey="cumulativeProgress"
                    stroke="#06d6a0"
                    strokeWidth={3}
                    dot={false}
                    name="Total Progress"
                  />

                  {/* Total target line */}
                  <Line
                    type="monotone"
                    dataKey="totalTarget"
                    stroke="#ef4444"
                    strokeWidth={2}
                    strokeDasharray="5 5"
                    dot={false}
                    name="Total Target"
                  />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Achievement Schedule */}
          <div className="space-y-3">
            <h4 className="text-lg font-orbitron text-neon-cyan flex items-center gap-2">
              <Clock className="w-5 h-5" />
              Goal Achievement Schedule
            </h4>

            <div className="space-y-3">
              {achievements.map((achievement, index) => (
                <div key={index} className="cosmic-card-inner p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <Badge 
                        variant="outline" 
                        className="border-2"
                        style={{ 
                          borderColor: priorityColors[achievement.priority], 
                          color: priorityColors[achievement.priority] 
                        }}
                      >
                        {achievement.priority.toUpperCase()}
                      </Badge>
                      <div>
                        <h5 className="font-semibold text-white">{achievement.goalName}</h5>
                        <p className="text-sm text-gray-400">{achievement.category} goal</p>
                      </div>
                    </div>

                    <div className="text-right">
                      <div className="text-lg font-bold text-neon-cyan">
                        Month {achievement.month}
                      </div>
                      <div className="text-sm text-gray-400">{achievement.date}</div>
                      <div className="text-sm font-medium text-stellar-gold">
                        ₹{achievement.amount.toLocaleString()}
                      </div>
                    </div>
                  </div>

                  <div className="mt-3 flex items-center justify-between text-xs text-gray-500">
                    <span>Individual Goal Target</span>
                    <span>Cumulative: ₹{achievement.cumulativeAmount.toLocaleString()}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Pie Chart Control */}
          <div className="pt-4 border-t border-gray-700">
            <Button
              onClick={() => setShowPieChart(!showPieChart)}
              variant="outline"
              className="w-full cosmic-button border-stellar-gold text-stellar-gold hover:bg-stellar-gold/10"
            >
              <PieChartIcon className="w-4 h-4 mr-2" />
              {showPieChart ? 'Hide' : 'Show'} Goals Distribution
            </Button>
          </div>

          {/* Goals Pie Chart */}
          {showPieChart && (
            <div className="mt-6 pt-6 border-t border-gray-700">
              <h4 className="text-lg font-orbitron text-stellar-gold flex items-center gap-2 mb-4">
                <PieChartIcon className="w-5 h-5" />
                Goals Distribution by Amount
              </h4>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Pie Chart */}
                <div className="h-80">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={goals.map((goal, index) => ({
                          name: goal.description,
                          value: goal.targetAmount,
                          color: categoryColors[goal.category],
                          priority: goal.priority
                        }))}
                        cx="50%"
                        cy="50%"
                        innerRadius={60}
                        outerRadius={120}
                        paddingAngle={5}
                        dataKey="value"
                      >
                        {goals.map((goal, index) => (
                          <Cell key={`cell-${index}`} fill={categoryColors[goal.category]} />
                        ))}
                      </Pie>
                      <Tooltip
                        contentStyle={{
                          backgroundColor: '#1F2937',
                          border: '1px solid #374151',
                          borderRadius: '8px',
                          color: '#F3F4F6'
                        }}
                        formatter={(value: any) => [`₹${value.toLocaleString()}`, 'Target Amount']}
                      />
                    </PieChart>
                  </ResponsiveContainer>
                </div>

                {/* Legend */}
                <div className="space-y-3">
                  <h5 className="text-sm font-medium text-gray-300">Goals by Category & Priority</h5>
                  {goals.map((goal, index) => (
                    <div key={index} className="flex items-center justify-between p-3 cosmic-card-inner">
                      <div className="flex items-center gap-3">
                        <div 
                          className="w-4 h-4 rounded"
                          style={{ backgroundColor: categoryColors[goal.category] }}
                        />
                        <div>
                          <div className="text-sm font-medium text-white">{goal.description}</div>
                          <div className="text-xs text-gray-400">{goal.category}</div>
                        </div>
                      </div>

                      <div className="text-right">
                        <Badge 
                          variant="outline" 
                          className="mb-1"
                          style={{ 
                            borderColor: priorityColors[goal.priority], 
                            color: priorityColors[goal.priority] 
                          }}
                        >
                          {goal.priority.toUpperCase()}
                        </Badge>
                        <div className="text-sm font-medium text-neon-cyan">
                          ₹{goal.targetAmount.toLocaleString()}
                        </div>
                        <div className="text-xs text-gray-400">
                          {((goal.targetAmount / goals.reduce((sum, g) => sum + g.targetAmount, 0)) * 100).toFixed(1)}%
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}


        </CardContent>
      </Card>
    </div>
  );
};

export default OverallGoalsTimeline;

