import { Card, CardContent } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { ArrowUpDown } from 'lucide-react';

interface ComparisonData {
  category: string;
  before: number;
  after: number;
  impact: number;
  unit?: string;
}

interface ComparisonTableProps {
  data: ComparisonData[];
}

export function ComparisonTable({ data }: ComparisonTableProps) {
  return (
    <Card className="glass-effect border-white/20">
      <CardContent className="p-8">
        <h3 className="font-orbitron text-2xl font-bold mb-6 text-center flex items-center justify-center">
          <ArrowUpDown className="mr-3 text-cosmic-400" />
          Transformation Analysis
        </h3>

        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow className="border-gray-600">
                <TableHead className="text-left text-white font-orbitron">Category</TableHead>
                <TableHead className="text-center text-red-400 font-medium">Before Optimization</TableHead>
                <TableHead className="text-center text-neon-green font-medium">After Optimization</TableHead>
                <TableHead className="text-center text-neon-cyan font-medium">Impact</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {data.map((row, index) => (
                <TableRow key={index} className="border-gray-700 hover:bg-white/5 transition-colors">
                  <TableCell className="font-medium text-white">{row.category}</TableCell>
                  <TableCell className="text-center text-gray-300">
                    {row.unit === 'currency' ? `₹${(row.before || 0).toLocaleString()}` : 
                     row.unit === 'months' ? `${row.before || 0} months` : 
                     (row.before || 0).toString()}
                  </TableCell>
                  <TableCell className="text-center text-gray-300">
                    {row.unit === 'currency' ? `₹${(row.after || 0).toLocaleString()}` : 
                     row.unit === 'months' ? `${row.after || 0} months` : 
                     (row.after || 0).toString()}
                  </TableCell>
                  <TableCell className="text-center">
                    <span className={`font-medium ${
                      (row.impact || 0) > 0 
                        ? (row.category.includes('Timeline') || row.category.includes('Debt') ? 'text-red-400' : 'text-neon-green')
                        : (row.impact || 0) < 0 
                        ? (row.category.includes('Timeline') || row.category.includes('Debt') ? 'text-neon-green' : 'text-red-400')
                        : 'text-gray-400'
                    }`}>
                      {(row.impact || 0) > 0 && !row.category.includes('Timeline') && !row.category.includes('Debt') ? '+' : ''}
                      {row.unit === 'currency' ? `₹${Math.abs(row.impact || 0).toLocaleString()}` : 
                       row.unit === 'months' ? `${row.impact || 0} months` : 
                       (row.impact || 0).toString()}
                    </span>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/WhatIfSi.content:
import { useState, useEffect } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Slider } from '@/components/ui/slider';
import { Calculator } from 'lucide-react';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { SimulationAdjustments } from '@/types/financial';

interface WhatIfSimulatorProps {
  questionnaireId: string;
  originalData: {
    preferred_savings: number;
    subscription_cost: number;
    entertainment_hours: number;
    shopping_monthly: number;
    dining_monthly: number;
  };
}

export function WhatIfSimulator({ questionnaireId, originalData }: WhatIfSimulatorProps) {
  const [adjustments, setAdjustments] = useState<SimulationAdjustments>({
    preferred_savings: originalData.preferred_savings,
    subscription_cost: originalData.subscription_cost,
    entertainment_hours: originalData.entertainment_hours,
    shopping_monthly: originalData.shopping_monthly,
    dining_monthly: originalData.dining_monthly,
  });

  const [simulationResults, setSimulationResults] = useState({
    savingsImpact: 0,
    goalTimeline: 36,
    investmentGrowth: 0,
  });

  const simulate = useMutation({
    mutationFn: async (adjustments: SimulationAdjustments) => {
      const response = await apiRequest('POST', '/api/simulate', {
        questionnaireId,
        adjustments,
      });
      return response.json();
    },
    onSuccess: (data) => {
      // Calculate impact based on simulation results
      const savingsIncrease = (adjustments.preferred_savings || 0) - originalData.preferred_savings;
      const subscriptionSavings = originalData.subscription_cost - (adjustments.subscription_cost || 0);
      const shoppingSavings = originalData.shopping_monthly - (adjustments.shopping_monthly || 0);
      const diningS = originalData.dining_monthly - (adjustments.dining_monthly || 0);

      const totalImpact = savingsIncrease + subscriptionSavings + shoppingSavings + diningS;
      const newTimeline = Math.max(12, data.goalTimeline?.timeToGoal || 36);
      const investmentGrowth = Math.round(totalImpact * 12 * 1.08);

      setSimulationResults({
        savingsImpact: totalImpact,
        goalTimeline: newTimeline,
        investmentGrowth,
      });
    },
  });

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      simulate.mutate(adjustments);
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [adjustments]);

  const handleAdjustment = (key: keyof SimulationAdjustments, value: number[]) => {
    setAdjustments(prev => ({ ...prev, [key]: value[0] }));
  };

  return (
    <Card className="glass-effect border-white/20">
      <CardContent className="p-8">
        <h3 className="font-orbitron text-2xl font-bold mb-6 text-center flex items-center justify-center">
          <Calculator className="mr-3 text-cosmic-400" />
          What-If Mission Simulator
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="space-y-6">
            {/* Savings Adjustment */}
            <div className="space-y-2">
              <Label className="text-gray-300">Monthly Savings Target</Label>
              <Slider
                value={[adjustments.preferred_savings || 0]}
                onValueChange={(value) => handleAdjustment('preferred_savings', value)}
                min={0}
                max={originalData.preferred_savings * 2}
                step={500}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-400">
                <span>₹0</span>
                <span className="text-neon-cyan font-medium">₹{adjustments.preferred_savings?.toLocaleString()}</span>
                <span>₹{(originalData.preferred_savings * 2).toLocaleString()}</span>
              </div>
            </div>

            {/* Subscription Reduction */}
            <div className="space-y-2">
              <Label className="text-gray-300">Subscription Costs</Label>
              <Slider
                value={[adjustments.subscription_cost || 0]}
                onValueChange={(value) => handleAdjustment('subscription_cost', value)}
                min={0}
                max={originalData.subscription_cost}
                step={100}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-400">
                <span>₹0</span>
                <span className="text-neon-cyan font-medium">₹{adjustments.subscription_cost?.toLocaleString()}</span>
                <span>₹{originalData.subscription_cost.toLocaleString()}</span>
              </div>
            </div>

            {/* Shopping Reduction */}
            <div className="space-y-2">
              <Label className="text-gray-300">Shopping Budget</Label>
              <Slider
                value={[adjustments.shopping_monthly || 0]}
                onValueChange={(value) => handleAdjustment('shopping_monthly', value)}
                min={0}
                max={originalData.shopping_monthly}
                step={200}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-400">
                <span>₹0</span>
                <span className="text-neon-cyan font-medium">₹{adjustments.shopping_monthly?.toLocaleString()}</span>
                <span>₹{originalData.shopping_monthly.toLocaleString()}</span>
              </div>
            </div>

            {/* Dining Reduction */}
            <div className="space-y-2">
              <Label className="text-gray-300">Dining Out Budget</Label>
              <Slider
                value={[adjustments.dining_monthly || 0]}
                onValueChange={(value) => handleAdjustment('dining_monthly', value)}
                min={0}
                max={originalData.dining_monthly}
                step={200}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-400">
                <span>₹0</span>
                <span className="text-neon-cyan font-medium">₹{adjustments.dining_monthly?.toLocaleString()}</span>
                <span>₹{originalData.dining_monthly.toLocaleString()}</span>
              </div>
            </div>
          </div>

          <div className="glass-effect p-6 rounded-lg">
            <h4 className="font-semibold mb-4 text-neon-cyan">Simulation Results</h4>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-300">Monthly Savings Impact:</span>
                <span className={`font-medium ${simulationResults.savingsImpact >= 0 ? 'text-neon-green' : 'text-red-400'}`}>
                  {simulationResults.savingsImpact >= 0 ? '+' : ''}₹{simulationResults.savingsImpact.toLocaleString()}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-300">Goal Achievement:</span>
                <span className="text-neon-cyan font-medium">{simulationResults.goalTimeline} months</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-300">Annual Investment Growth:</span>
                <span className="text-cosmic-400 font-medium">₹{simulationResults.investmentGrowth.toLocaleString()}</span>
              </div>
            </div>

            {simulate.isPending && (
              <div className="mt-4 text-center text-gray-400">
                <div className="animate-pulse">Calculating scenarios...</div>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/questionnaire/Goal.content:
import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Plus, X, Target, Clock, Star } from 'lucide-react';

interface FinancialGoal {
  description: string;
  target_amount: number;
  timeline_months: number;
  priority: "high" | "medium" | "low";
  category: "emergency" | "investment" | "purchase" | "retirement" | "education" | "other";
}

interface GoalBuilderProps {
  value: FinancialGoal[];
  onChange: (goals: FinancialGoal[]) => void;
}

export function GoalBuilder({ value, onChange }: GoalBuilderProps) {
  const [goals, setGoals] = useState<FinancialGoal[]>(value.length > 0 ? value : [{
    description: "",
    target_amount: 500000,
    timeline_months: 24,
    priority: "high",
    category: "emergency"
  }]);

  const addGoal = () => {
    const newGoal: FinancialGoal = {
      description: "",
      target_amount: 100000,
      timeline_months: 12,
      priority: "medium",
      category: "other"
    };
    const updatedGoals = [...goals, newGoal];
    setGoals(updatedGoals);
    onChange(updatedGoals);
  };

  const removeGoal = (index: number) => {
    const updatedGoals = goals.filter((_, i) => i !== index);
    setGoals(updatedGoals);
    onChange(updatedGoals);
  };

  const updateGoal = (index: number, field: keyof FinancialGoal, value: any) => {
    const updatedGoals = goals.map((goal, i) => 
      i === index ? { ...goal, [field]: value } : goal
    );
    setGoals(updatedGoals);
    onChange(updatedGoals);
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'high': return <Star className="w-4 h-4 text-red-400" />;
      case 'medium': return <Star className="w-4 h-4 text-yellow-400" />;
      case 'low': return <Star className="w-4 h-4 text-green-400" />;
      default: return <Star className="w-4 h-4 text-gray-400" />;
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <Label className="text-gray-300 font-semibold">Financial Goals</Label>
        <Button
          type="button"
          onClick={addGoal}
          variant="outline"
          size="sm"
          className="text-neon-cyan border-neon-cyan hover:bg-neon-cyan/10"
        >
          <Plus className="w-4 h-4 mr-1" />
          Add Goal
        </Button>
      </div>

      {goals.map((goal, index) => (
        <Card key={index} className="cosmic-card border-slate-600">
          <CardHeader className="pb-3">
            <div className="flex items-center justify-between">
              <CardTitle className="text-sm font-orbitron text-cosmic-gradient flex items-center gap-2">
                <Target className="w-4 h-4" />
                Goal {index + 1}
                {getPriorityIcon(goal.priority)}
              </CardTitle>
              {goals.length > 1 && (
                <Button
                  type="button"
                  onClick={() => removeGoal(index)}
                  variant="ghost"
                  size="sm"
                  className="text-red-400 hover:bg-red-400/10"
                >
                  <X className="w-4 h-4" />
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label className="text-gray-300 text-sm">Goal Description</Label>
              <Input
                value={goal.description}
                onChange={(e) => updateGoal(index, 'description', e.target.value)}
                placeholder="e.g., Emergency Fund, House Purchase"
                className="bg-gray-800/50 border-gray-600 focus:border-neon-cyan text-white"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label className="text-gray-300 text-sm flex items-center gap-1">
                  <Target className="w-3 h-3" />
                  Target Amount (₹)
                </Label>
                <Input
                  type="number"
                  value={goal.target_amount}
                  onChange={(e) => updateGoal(index, 'target_amount', parseFloat(e.target.value) || 0)}
                  placeholder="1000000"
                  className="bg-gray-800/50 border-gray-600 focus:border-neon-cyan text-white"
                />
              </div>

              <div>
                <Label className="text-gray-300 text-sm flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  Timeline (Months) - Max 20 years
                </Label>
                <Input
                  type="number"
                  value={goal.timeline_months}
                  onChange={(e) => {
                    const value = parseFloat(e.target.value) || 1;
                    const clampedValue = Math.min(Math.max(value, 1), 600);
                    updateGoal(index, 'timeline_months', clampedValue);
                  }}
                  placeholder="24"
                  min="1"
                  max="240"
                  className="bg-gray-800/50 border-gray-600 focus:border-neon-cyan text-white"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label className="text-gray-300 text-sm">Priority</Label>
                <Select
                  value={goal.priority}
                  onValueChange={(value: any) => updateGoal(index, 'priority', value)}
                >
                  <SelectTrigger className="bg-gray-800/50 border-gray-600 focus:border-neon-cyan text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="high">High Priority</SelectItem>
                    <SelectItem value="medium">Medium Priority</SelectItem>
                    <SelectItem value="low">Low Priority</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-gray-300 text-sm">Category</Label>
                <Select
                  value={goal.category}
                  onValueChange={(value: any) => updateGoal(index, 'category', value)}
                >
                  <SelectTrigger className="bg-gray-800/50 border-gray-600 focus:border-neon-cyan text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="emergency">Emergency Fund</SelectItem>
                    <SelectItem value="purchase">Major Purchase</SelectItem>
                    <SelectItem value="investment">Investment</SelectItem>
                    <SelectItem value="retirement">Retirement</SelectItem>
                    <SelectItem value="education">Education</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

